mapper.update:
UPDATE sys_env_threshold
SET
max_value = CASE env_parameter_type

    WHEN #{item.envParameterType} THEN #{item.max}

END,
min_value = CASE env_parameter_type

    WHEN #{item.envParameterType} THEN #{item.min}

END,
update_time = NOW()
WHERE greenhouse_env_code = #{envCode}
AND env_parameter_type IN

    #{item.envParameterType}
